import React from 'react'
import { useState, useEffect } from "react"
import axios from 'axios'

import {Link} from 'react-router-dom'










const AdminDashboard = () => {



axios.defaults.withCredentials = true;
axios.defaults.headers.post['Content-Type'] = 'application/json';
axios.defaults.headers.post['Accept'] = 'application/json'; 


const [dt, setDt] = useState(new Date().toLocaleString());

useEffect(() => {

    fetchWeeklyData();

    let secTimer = setInterval( () => {
      setDt(new Date().toLocaleString())
    },1000)

    return () => clearInterval(secTimer);
}, []);



    const [response, setResponse] = useState([]);



    const fetchWeeklyData = async() => {
          const raw_response = await axios.get(`/api/dashboard/overview`);
          const rental_data = await raw_response.data.kolcsonzesek;
          setResponse(rental_data);
          setCarRentals(rental_data);
          
          setLoading(false);

    };

















    return (
        <div className="dashboard-container">
        <div className="admin_side_panel">
            <ul className="admin_links_list">
                    <Link to="/admin/dashboard">
                    <button className="admin_links_active">Kezdőlap</button>
                    </Link>
                    

                     <Link to="/admin/dashboard/kolcsonzesek">
                    <button className="admin_links">Kölcsönzések</button>
                     </Link>

                    <Link to="/admin/dashboard/ajandekbolt">
                    <button className="admin_links">Ajándékbolt</button>
                     </Link>

                     <Link to="/admin/dashboard/dolgozok">
                    <button className="admin_links">Dolgozók</button>
                    </Link>
                    

                    <button className="admin_links">Kijelentkezés</button>
            </ul>
        </div>
        <div className="admin_content">

        <div className="overview_panel">
                <h1>Heti összegzés</h1>
                    <p>Üdv a rendszerben, Jack Teszter!</p>
                    <p>{dt}</p>
                   
                    <table className="admin_ov_table">
                        <tr>
                            <th>A heti bevétel:</th>
                            <th>Heti kölcsönzések:</th>
                        </tr>
                                <tr>
                                    <td>327 000 Ft</td>
                                    <td>4db</td>
                                </tr>
                    </table>

                    <table className="admin_ov_table">
                        <tr>
                             <th>Kölcsönzés alatt álló modellek:</th>
                        </tr>
                                <tr>
                                    <td>Audi A4 </td>
                                </tr>
                    </table>
            </div>



        </div>
            
        </div>
    )
}

export default AdminDashboard
