const AppWrapper = () => {
const[isAuth,setIsAuth] = useState(false);


const CheckAuth = () => {

           if(localStorage.getItem('auth_token')) {

                  setIsAuth(true);


                  } else {

                  setIsAuth(false);
                      
                  }
}


        CheckAuth();

            if(isAuth === true) {

                return <Redirect to="/kolcsonzes" component={CarRentalLoggedin} />
            }
            
            else {

                return <Redirect to="/"  component={Home} />
            }










    return (
        <div>
            
        </div>
    )
}

export default AppWrapper
