import React from 'react'
import { useState, useEffect } from "react"
import axios from 'axios'
import edit_icon from '../components/imgs/edit_icon.png'
import {Link} from 'react-router-dom'








const AdminDashboard = () => {

axios.defaults.withCredentials = true;
axios.defaults.headers.post['Content-Type'] = 'application/json';
axios.defaults.headers.post['Accept'] = 'application/json'; 


const [dt, setDt] = useState(new Date().toLocaleString());

useEffect(() => {
    let secTimer = setInterval( () => {
      setDt(new Date().toLocaleString())
    },1000)

    return () => clearInterval(secTimer);
}, []);


var default_content = ""


const [panelContent, setPanelContent] = useState();
const [rentalList, setrentalList] = useState([]);


if (typeof panelContent !== "undefined"){
 
    default_content = "";
} else {
    default_content = (
                <div className="overview_panel">
                <h1>Heti összegzés</h1>
                    <p>Üdv a rendszerben, Jack Teszter!</p>
                    <p>{dt}</p>
                   




                    <table className="admin_ov_table">
                        <tr>
                            <th>A heti bevétel:</th>
                            <th>Heti kölcsönzések:</th>
                        </tr>
                                <tr>
                                    <td>327 000 Ft</td>
                                    <td>4db</td>
                                </tr>
                    </table>

                    <table className="admin_ov_table">
                        <tr>
                             <th>Kölcsönzés alatt álló modellek:</th>
                        </tr>
                                <tr>
                                    <td>Audi A4 </td>
                                </tr>
                    </table>
            </div>

);
}



const handleKolcsonzesek = () => {
    setPanelContent(kolcsonzesek);
}

const handleKezdolap = () => {

    setPanelContent(undefined);

}
            useEffect(() => {
                   fetchRentals();
                    
                }, []);

const fetchRentals = async() => {
          const rental_data_raw = await axios.get(`/api/dashboard/kolcsonzesek`);
          const rental_data = await rental_data_raw.data.kolcsonzesek;
        
          setrentalList(rental_data);

    };



const kolcsonzesek = () => {











return (

<div className="overview_panel">

                <h1>Kölcsönzések</h1>
                <br />
                <table className="admin-kolcsonzesek">
                    <tr>
                        <th>Kölcsönzés azonosító</th>
                        <th>Kölcsönző neve</th>
                        <th>Kölcsönző telefonszáma</th>
                        <th>Kölcsönzött modell</th>
                        <th>Kölcsönzés kezdete</th>
                        <th>Kölcsönzés vége</th>
                        <th>Státusz</th>
                        <th>Fizetendő</th>
                        <th>Műveletek</th>
                    </tr>
                    {rentalList.map(rentals => (
                    <tr key={rentals.id}>
                    <td>{rentals.kolcsonzes_id}</td>
                    <td>{rentals.kolcsonzo_vezeteknev + " " + rentals.kolcsonzo_keresztnev}</td>
                    <td>{rentals.kolcsonzo_telefon}</td>
                    <td>{rentals.kolcsonzott_modell}</td>
                    <td>{rentals.kolcsonzes_start}</td>
                    <td>{rentals.kolcsonzes_end}</td>
                    <td>{rentals.statusz}</td>
                    <td>{rentals.fizetendo}</td>
                    <td>
                    <Link to={`edit-rental/${rentals.id}`}>
                    <button className="edit-button">Szerkesztés <img className="edit-icon" src={edit_icon} alt="" /> </button>
                    </Link>
                    </td>
                    

                    </tr>
            
            
            )

        )}
                




                </table>




</div>



)};


























    console.log(rentalList);


    return (
        <div className="dashboard-container">
        <div className="admin_side_panel">
            <ul className="admin_links_list">
                    <button onClick={handleKezdolap} className="admin_links">Kezdőlap</button>
                    <button onClick={handleKolcsonzesek} className="admin_links">Kölcsönzések</button>
                    <button className="admin_links">Ajándékbolt</button>
                    <button className="admin_links">Modellek</button>
                    <button className="admin_links">Dolgozók</button>
                    <button className="admin_links">Kijelentkezés</button>
            </ul>
        </div>
        <div className="admin_content">
            {default_content}
            {panelContent}


        </div>
            
        </div>
    )
}

export default AdminDashboard
