import React from 'react'
import mini_logo from '../components/imgs/logo.png'
import {useState} from 'react'
import axios from 'axios'
import Swal from 'sweetalert2'
import {useHistory} from 'react-router-dom'


const AdminLogin = () => {

  const history = useHistory();
  const [loginInput, setLogin] = useState({

  email_phone: '',
  password: '',
  error_list: [],

  });


    const handleInput = (e) => {

      e.persist();
      setLogin({...loginInput, [e.target.name]: e.target.value });
    }


    const loginSubmit = (e) => {

      e.preventDefault();
      
      const data = {

          email_phone: loginInput.email_phone,
          password: loginInput.password,

      }

       axios.get('/sanctum/csrf-cookie').then(response => {
       axios.post(`api/login`, data).then(res => {


         if(res.data.status === 200)
         {
                    localStorage.setItem('auth_token', res.data.token);
                    localStorage.setItem('phone', res.data.phone);
                    localStorage.setItem('user_id', res.data.user_id);
                    localStorage.setItem('v_nev', res.data.v_nev);
                    localStorage.setItem('k_nev', res.data.k_nev);
                    localStorage.setItem('auth_name', res.data.username);
                    
                      Swal.fire(
                        res.data.message,
                        '',
                        'success'
                      )

                  
                      history.push('/kolcsonzes');
                     

         }

         else if(res.data.status === 401)
         {
                      Swal.fire(
                        res.data.message,
                        '',
                        'error'
                      )

         }

         else 
         {

           setLogin({...loginInput, error_list: res.data.validation_errors });

         }
         

             
       });
});


 }













    return (
        <div className="admin_login_container" >
                <div className="panel_blur"></div>
                <div className="panel2">
                <div className="admin_login_form">
                
                            <form>
                                <img className="logo_admin_panel" src={mini_logo} alt="" />
                                <br />
                                <label htmlFor="">Alkalmazotti azonosító:</label>
                                <br />
                                <input type="text" name="email_phone"   Placeholder="Alkalmazotti azonosító" />
                                <br />
                                <span className="error_message"></span>
                                <br />
                                 <label htmlFor="">Jelszó:</label>
                                <br />
                                <input type="password" name="password" Placeholder="Jelszó" />
                                <span className="error_message"></span>
                                <br />
                                <button className="primary_button">Bejelentkezés</button>

                                <br />
                                <br />
                             </form>

                    </div>
                </div>
        </div>
    )
}

export default AdminLogin
