import React from 'react'
import Navbar from '../components/Navbar'
import Footer from '../components/Footer'
import Banner from '../components/Banner'
import CarCard from '../components/CarCard'
import NavbarLoggedIn from '../components/NavbarLoggedin'
import FooterLoggedIn from '../components/FooterLoggedIn'
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import { withStyles } from '@material-ui/core/styles';
import Select from '@material-ui/core/Select';
import createTheme from '@material-ui/core/styles'

import {createMuiTheme, ThemeProvider} from '@material-ui/core';
import axios from 'axios'
import {useState, useEffect} from 'react'



const OurModels = () => {



    var AuthNavbar = '';
    var AuthFooter = '';


if(localStorage.getItem('auth_token'))

{

    AuthNavbar = (
     <NavbarLoggedIn/>
                );

    AuthFooter = (
     <FooterLoggedIn/>
                );


} else {

  AuthNavbar = (
     <Navbar/>
                );

    AuthFooter = (
     <Footer/>
                );            

    
}




const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#9c1a1a00',
    }
  },
  text: {
    primary: '#9c1a1a93'
  }
})


const useStyles = makeStyles((theme) => ({

  formControl: {
    margin: theme.spacing(5),
    minWidth: 200,
    padding: '0px',
    border: '1px solid 9c1a1a',
    background: 'transparent',
    color: 'white',
  },

  Label: {
  
  color: 'white'


  },

}));

const StyledMenuItem = withStyles((theme) => ({
  root: {
    '&$disabled': {
      color: 'black',
      background: 'black',
      
    },
    background: 'transparent',
    borderRadius: 3,
    border: 0,
    color: 'black',
    height: 50,
    padding: '0 20px',
   boxShadow: '0 3px 5px 2px rgba(255, 255, 255, .3)',
    margin: "20px 0px 0px 0px",
    fontSize: '20px',
      '&:hover': {
        color: 'white'
      }
  },
  
}))(MenuItem);

const StyledSelect = withStyles((theme) => ({
  root: {
    background: '#1717176e',
    borderRadius: 3,
    border: 0,
    color: 'white',
    height: 'auto',
    padding: '20px',
    boxShadow: '0px 5px 8px #1717176e',
    margin: "auto",
    fontSize: '20px',

  },
  
}))(Select);


/////////////////////// Adatok lekérdezése az adatbázisból
      useEffect(() => {
          fetchData();
      }, []);


const [cars, setCars] = useState ([]);

const ListCars = (e) => {



const data = {

          car_category: e,
      }

axios.get('/sanctum/csrf-cookie').then(res => {
axios.post('api/modelljeink', data).then(res => {

    const fetchData = async() => {
          const car_data = await axios.post('api/modelljeink', data);
          const car_info = await car_data.data.cars;
          setCars(cars);

    };







///////////////////////////////////////////////////////
}


)
 });



}


const classes = useStyles();
  const [value, setValue,] = React.useState('');
  const [open, setOpen] = React.useState(false);

  const handleChange = (event) => {
    setValue(event.target.value);
    ListCars(event.target.value);



  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };


  console.log(cars);

  return (
    <div className="modelljeink_container">
      <div className="empty_space"></div>

      {AuthNavbar}

      <Banner title="Modelljeink" />

      <div className="filter_container">

        <form>


        <ThemeProvider theme={theme}>
        <FormControl variant="outlined" className={classes.formControl}>
        <StyledSelect
          open={open}
          onClose={handleClose}
          onOpen={handleOpen}
          value={value}
          onChange={handleChange}
          displayEmpty
         
        >
          <StyledMenuItem name='car_category' value={''} disabled>Kategória</StyledMenuItem>
          <StyledMenuItem name='car_category' value={'sedan'}>Sedan</StyledMenuItem>
          <StyledMenuItem name='car_category' value={'kombi'}>Kombi</StyledMenuItem>
          <StyledMenuItem name='car_category' value={'hibrid'}>Hibrid</StyledMenuItem>
          <StyledMenuItem name='car_category' value={'furgon'}>Furgon</StyledMenuItem>

        </StyledSelect>
  
      </FormControl>
</ThemeProvider>
  
         </form>
      
      </div>


    <div className="carlist_container">

                      {cars.map(car => (



                        <CarCard key={car.id} id={car.id} car_name={car.car_name} picture={car.car_picture}         />
                        



                      ))}


        </div>


      {AuthFooter}


    </div>
  )
}

export default OurModels
