/* eslint-disable no-unused-vars */

/// Imports....
import React, { useState } from 'react'
import Swiper from 'react-id-swiper'
import './App.scss'
import './image-gallery.scss'
import Slider from './components/Slider'
import Navbar from './components/Navbar'
import Banner from './components/Banner'
import LoginForm from './components/LoginForm'
import Footer from './components/Footer'
import { BrowserRouter as Router, Switch, Link, Route, Redirect, useHistory } from 'react-router-dom'
import About from './pages/About'
import CountUp from 'react-countup'
import LoyaltyProgram from './pages/LoyaltyProgram'
import Register from './pages/Register'
import Login from './pages/Login'
import OurModels from './pages/OurModels'
import ScrollToTop from './components/scrollToTop'
import Expand from './components/CarCardExpand'
import GiftShop from './pages/GiftShop'
import CarRental_LoggedIn from './pages/CarRental_LoggedIn'
import Dashboard from './pages/Dashboard'
import axios from 'axios';
import NavbarLoggedIn from './components/NavbarLoggedin'
import FooterLoggedIn from './components/FooterLoggedIn'
import ProtectedRoute from './components/ProtectedRoute'
import UserRentals from './pages/UserRentals'
import AdminLogin from './pages/AdminLogin'
import AdminDashboard from './pages/AdminDashboard'
import EditRentalRow from './pages/EditRentalRow'
import AdminRental from './pages/AdminRental'
import AdminGiftStore from './pages/AdminGiftStore'
import AdminEmployees from './pages/AdminEmployees'
import AddEmployee from './pages/AddEmployee'
import AdminRoute from './components/AdminRoute'


axios.defaults.withCredentials = true;
axios.defaults.baseURL = "http://localhost:8000";
axios.defaults.headers.post['Content-Type'] = 'application/json';
axios.defaults.headers.post['Accept'] = 'application/json';
axios.interceptors.request.use(function(config){

    const token = localStorage.getItem('auth_token'); 
    config.headers.Authorization = token ? `Bearer ${token}` : '';
    return config;
});







// Homepage

const Home = () => {

const history = useHistory();


if(localStorage.getItem('auth_token')) {

   history.push('/kolcsonzes');
   window.location.reload(false);

}

    var AuthNavbar = '';
    var AuthFooter = '';

if(localStorage.getItem('auth_token'))

{

    AuthNavbar = (
     <NavbarLoggedIn/>
                );

    AuthFooter = (
     <FooterLoggedIn/>
                );


} else {

  AuthNavbar = (
     <Navbar/>
                );

    AuthFooter = (
     <Footer/>
                );            


}


  return (
    <div className="front_end">

     {AuthNavbar}
      <div className="slider_bg">
      <Slider />

      </div>
      <Banner title="Autókölcsönzés" />

      <LoginForm form_text="Az autókölcsönzéshez be kell jelentkeznie!" />

      {AuthFooter}

    </div>
  )
}



const App = () => {

const user_id = localStorage.getItem('user_id');

var isAuth = false;
const [admin, setAdmin] = useState(false);

if(localStorage.getItem('admin')) {

  setAdmin(true);

} else {

  setAdmin(false);
}







if(localStorage.getItem('auth_token'))

{

isAuth = true;


} else {

isAuth = false;

}





  return (
    <div className="front_end">
      <Router>
        <ScrollToTop />
        <Switch>

          <Route path="/" exact component={()=> <Home authorized={isAuth}/>} />
          <Route path="/rolunk" exact component={About} />
          <Route path="/husegprogram" exact component={LoyaltyProgram} />
          <Route path="/login" exact component={Login} />
          <Route path="/regisztracio" exact component={Register} />
          <Route path="/modelljeink" exact component={OurModels} />
          <Route path="/admin" exact component={AdminLogin} />


          <Route path="/admin/dashboard/dolgozok/ujdolgozo" exact component={AddEmployee} />
          <Route path="/admin/dashboard/dolgozok" exact component={AdminEmployees} />
          <Route path="/admin/dashboard/ajandekbolt" exact component={AdminGiftStore} />
          <Route path="/admin/dashboard/kolcsonzesek" exact component={AdminRental} />
          {/* <Route path="/admin/dashboard" exact component={AdminDashboard} /> */}
          <Route path="/admin/dashboard/kolcsonzesek/edit-rental/:rental_id" exact component={EditRentalRow} />
          <Route path="/admin/delete-employee/:id" />
          <Route path="/checkdates/:model_id" />
          <Route path="/users/:user_id" />
          <Route path="/kolcsonzesek/:user_id" exact component={UserRentals}/>
          <Route path="/modelljeink/:id" exact component={Expand} />

          <AdminRoute path="/admin/dashboard" component={AdminDashboard} isAdmin={admin}/>
          <ProtectedRoute path="/kolcsonzes" component={CarRental_LoggedIn} isAuth={isAuth} />
          <ProtectedRoute path="/ajandekbolt" component={GiftShop} isAuth={isAuth} />


        </Switch>
      </Router>
    </div>
  )
}

export default App
