import React from 'react'
import { useState, useEffect } from "react"
import axios from 'axios'








const AdminDashboard = () => {

const [dt, setDt] = useState(new Date().toLocaleString());

useEffect(() => {
    let secTimer = setInterval( () => {
      setDt(new Date().toLocaleString())
    },1000)

    return () => clearInterval(secTimer);
}, []);


var default_content = ""


const [panelContent, setPanelContent] = useState();
const [rentalList, setrentalList] = useState([]);


if (typeof panelContent !== "undefined"){
 
    default_content = "";
} else {
    default_content = (
                <div className="overview_panel">
                <h1>Heti összegzés</h1>
                    <p>Üdv a rendszerben, Jack Teszter!</p>
                    <p>{dt}</p>
                   




                    <table className="admin_ov_table">
                        <tr>
                            <th>A heti bevétel:</th>
                            <th>Heti kölcsönzések:</th>
                        </tr>
                                <tr>
                                    <td>327 000 Ft</td>
                                    <td>4db</td>
                                </tr>
                    </table>

                    <table className="admin_ov_table">
                        <tr>
                             <th>Kölcsönzés alatt álló modellek:</th>
                        </tr>
                                <tr>
                                    <td>Audi A4 </td>
                                </tr>
                    </table>
            </div>

);
}



const handleKolcsonzesek = () => {
    setPanelContent(kolcsonzesek);
}

const handleKezdolap = () => {

    setPanelContent(undefined);

}




const kolcsonzesek = () => {

const fetchRentals = async() => {
          const rental_data_raw = await axios.get(`/api/dashboard/kolcsonzesek`);
          const rental_data = await rental_data_raw.kolcsonzesek;
          setrentalList(rental_data);

    };

fetchRentals();





return (

<div className="overview_panel">
                <h1>Kölcsönzések</h1>
                <table className="admin_ov_table">
                    <tr>
                        <th>Kölcsönzés azonosító</th>
                        <th>Kölcsönző neve</th>
                        <th>Kölcsönző telefonszáma</th>
                        <th>Kölcsönzött modell</th>
                        <th>Kölcsönzés kezdete</th>
                        <th>Kölcsönzés vége</th>
                        <th>Fizetendő</th>
                    </tr>
                </table>






</div>



)};





























    return (
        <div className="dashboard-container">
        <div className="admin_side_panel">
            <ul className="admin_links_list">
                    <button onClick={handleKezdolap} className="admin_links">Kezdőlap</button>
                    <button onClick={handleKolcsonzesek} className="admin_links">Kölcsönzések</button>
                    <button className="admin_links">Ajándékbolt</button>
                    <button className="admin_links">Modellek</button>
                    <button className="admin_links">Dolgozók</button>
                    <button className="admin_links">Kijelentkezés</button>
            </ul>
        </div>
        <div className="admin_content">
            {default_content}
            {panelContent}


        </div>
            
        </div>
    )
}

export default AdminDashboard
