// }





// Homepage

const Home = () => {

              var isAuth = false;

          if(localStorage.getItem('auth_token'))

          {

          isAuth = true;





          } else {

          isAuth = false;

          }

    var AuthNavbar = '';
    var AuthFooter = '';

if(localStorage.getItem('auth_token'))

{

    AuthNavbar = (
     <NavbarLoggedIn/>
                );

    AuthFooter = (
     <FooterLoggedIn/>
                );


} else {

  AuthNavbar = (
     <Navbar/>
                );

    AuthFooter = (
     <Footer/>
                );            


}


  return (
    <div className="front_end">

     {AuthNavbar}

      <Slider />

      <Banner title="Autókölcsönzés" />

      <LoginForm form_text="Az autókölcsönzéshez be kell jelentkeznie!" />

      {AuthFooter}

    </div>
  )
}


const App = () => {

var isAuth = false;

if(localStorage.getItem('auth_token'))

{

isAuth = true;





} else {

isAuth = false;

}





  return (
    <div className="front_end">
      <Router>
        <ScrollToTop />
        <Switch>


          <ProtectedRoute path="/kolcsonzes" component={CarRental_LoggedIn} isAuth={isAuth} />
          <ProtectedRoute path="/ajandekbolt" component={GiftShop} isAuth={isAuth} />

          <Route path="/" exact component={Home} />
          <Route path="/rolunk" exact component={About} />
          <Route path="/husegprogram" exact component={LoyaltyProgram} />
          <Route path="/login" exact component={Login} />
          <Route path="/regisztracio" exact component={Register} />
          <Route path="/modelljeink" exact component={OurModels} />
          <Route path="/modelljeink/:id" exact component={Expand} />



        </Switch>
      </Router>
    </div>
  )
}

export default App
